import React, { Component } from "react";
import { fetchApi } from "../action/Action";
import { connect } from "react-redux";
import { List, Avatar } from 'antd';
import 'antd/dist/antd.less';
import { ListGroup, ListGroupItem, Media, CardImg, Input } from 'reactstrap';
import './ListView.css'


class ListView extends Component {

    constructor(props) {
        super(props);
        this.state = {
            data: []
        };
    }

    componentDidMount() {
        this.props.fetchApi()
    }

    componentWillReceiveProps(nextProps) {
        if (nextProps.reducerListData != this.state.data) {
            this.setState({ data: nextProps.reducerListData })

        }
    }

    handleFilter=(e)=>{
        var filterList = reducerListData && reducerListData.ListApiData && reducerListData.ListApiData.length > 0 ? reducerListData.ListApiData :[]
        filterList.filter((list)=>{
            return list.toLowerCase().search(
                e.target.value.toLowerCase()) !== -1;
        }) 
        this.setState({data:filterList})
    }



    render() {
        const { reducerListData } = this.props

        return (
            <div>
                 <Input className="input" placeholder="Search..." onChange={this.handleFilter} ></Input>
                {reducerListData && reducerListData.ListApiData && reducerListData.ListApiData.length > 0 ? reducerListData.ListApiData.map(item => {
                    return (
                        <ListGroup key={item.name} className="ListView">
                            <ListGroupItem>
                                <Media>
                                    <Media left href="#">
                                        {/* <Media object data-src={item.avatar} alt={item.avatar} /> */}
                                        <CardImg top width="100%" src={item.avatar} alt="Card image cap" />
                                    </Media>
                                    <Media body>
                                        <Media heading>
                                            {item.name}
                                        </Media>
                                        {item.email}
                                    </Media>
                                </Media>
                            </ListGroupItem>
                        </ListGroup>
                    )
                }) : 'null'}

            </div>
        );
    }
}

const mapStateToProps = state => {
    console.log('state', state)
    return {
        reducerListData: state
    };
};

const mapDispatchToProps = dispatch => {
    console.log('dispatch', dispatch)
    return {
        fetchApi: () => dispatch(fetchApi())
    };
};



export default connect(
    mapStateToProps,
    mapDispatchToProps
)(ListView);
