import React, { Component } from "react";
import { fetchApi } from "../action/Action";
import { connect } from "react-redux";
import { List, Avatar } from 'antd';

class ListView extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: []
        };
    }

    componentDidMount() {
        this.props.fetchApi()
    }
    // componentWillReceiveProps(props){
    //     this.setState({data:this.props.reducerListData})
    // }

    render() {

        const { reducerListData, data } = this.props
        console.log('reducerList',data)
        return (
            <div>
                <h1>Welcome to ListView page</h1>
                {this.props.reducerListData ? this.props.reducerListData.mapDispatchToProps(item => {
                    return (
                        <List
                            itemLayout="horizontal"
                            dataSource={item}
                            renderItem={item => (
                                <List.Item>
                                    <List.Item.Meta
                                        avatar={<Avatar src={item.avatar} />}
                                        title={<>{item.name}</>}
                                        description={item.email}
                                    />
                                </List.Item>
                            )}
                        />
                    )
                }) :''}

            </div>
        );
    }
}

const mapStateToProps = state => {
    console.log('state', state)
    return {
        reducerListData: state
    };
};

const mapDispatchToProps = dispatch => {
    console.log('dispatch', dispatch)
    return {
        fetchApi: () => dispatch(fetchApi())
    };
};



export default connect(
    mapStateToProps,
    mapDispatchToProps
)(ListView);
